import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'

export function RecipeList( {recipes} ){
  console.log(recipes);
  return (
    recipes.map((recipe, i) => {
      return <Recipe 
				name={recipe.name}
				img={recipe.img}
				ingredients={recipe.ingredients}
				directions={recipe.directions}
				description={recipe.description}
        index = {i}
			/>
    })
  )
}

const removeRecipe = (id) => document.getElementById(id).remove();

function Recipe({name, img, ingredients, description, directions, index}){
  return (
    <Container>
    <div id={index}>
      <style>
        {
        `.btn-flat {
          background-color: indigo;
          color: white;
        }
        .btn-xxl {
          padding: 1rem 1.5rem;
          font-size: 1.5rem;
        }`}
      </style>
      <Row><p><b>Name:</b> {name}</p></Row>
      <p><b>Description:</b> {description}</p>
			<p><b>Ingredients:</b> {ingredients}</p>
			<p><b>Directions:</b> {directions}</p>
      <img height={200} src={img} alt={description} />
      <br/>
      <Button variant='flat' size='xxl' onClick={() => removeRecipe(index)}>Remove Recipe</Button>
      <p><b>----------------------------------------------------------------</b></p>
    </div>
    </Container>
  )
}
