import './App.css';
import { Link } from "react-router-dom";
import { useState, useEffect } from 'react';
import { RecipeList } from './recipes';
import 'bootstrap/dist/css/bootstrap.min.css';
import Stack from 'react-bootstrap/Stack'


export function App() {

  const [recipes, setRecipes] = useState([]);
  let newRecipeList = [];
  newRecipeList = JSON.parse(localStorage.getItem("storedRecipes"));

  function fetchJson (){
    fetch('./allRecipes.json')
      .then(response => response.json())
      .then(setRecipes)
      .catch( e => console.log(e.message));
  }

  useEffect(() => {
    fetchJson();
  }, []);

  if (recipes == null) return;
  console.log(newRecipeList);
  console.log(recipes);
  return (
    <div>
      <nav>
        <Link to="/add">AddRecipe</Link>
      </nav>
      <h1 class="text-center">Recipe Website</h1>
      <Stack class="p-3 mb-2 bg-info text-dark" gap={3} background-color="#20c997">
        <RecipeList recipes={[...recipes, ...newRecipeList]}/>
      </Stack>
    </div>
  );
}
