let i = 0;

export function RecipeList( {recipes} ){
  return (
    recipes.map((recipe, i) => {
      return <Recipe 
				name={recipe.name}
				img={recipe.img}
				ingredients={recipe.ingredients}
				directions={recipe.directions}
				description={recipe.description}
			/>
    })
  )
}

function removeRecipe(btn){
  const butn = document.getElementById(btn)
  return butn.parentElement.remove();
}

function Recipe({name, img, ingredients, description, directions}){
  i++;
  let newId = "btn"+i;
  return (
    <div id={newId}>
      <p><b>Name:</b> {name}</p>
      <p><b>Description:</b> {description}</p>
			<p><b>Ingredients:</b> {ingredients}</p>
			<p><b>Directions:</b> {directions}</p>
      <img height={200} src={img} alt={description} />
      <button  type="button" onClick={newId.remove()}>Remove Recipe</button>
      <p><b>----------------------------------------------------------------</b></p>
    </div>
  )
}
